print(42 > 12) # Cela vérifi si 42 est supérieur à 12. Ca retourne vrai si c'est supérieur et faux si c'est inférieur => return True
# print(12 = 12) # Impossible à compilé car "=" est une affectation et on ne peux pas affecter un int avec un int seul les variables peuvent être affectées
print("hello" == "world") # Cela vérifi si les deux valeurs sont égale car "==" est un élément de comparaison vrai si ce sont les mêmes valeurs faux dans le cas contraire => return False
print(218 >= 118) # Cela vérifi si 218 est supérieur ou égale à 118. Ca retourne vrai ci c'est le cas faux dans le cas contraire => return True
print("a".upper() == "A") # upper() permet de mettre en majuscule et "==" permet de comparer donc vrai dans ce cas > return True
print(1 * 2 * 3 * 4 <= 9) # Cela vérifi si le résultat de la multiplication est inférieur ou égale à 9 faux dans ce cas  => return False
print("z" in "azerty") # Cela vérifi si la lettre "z" est contenu dans le mot "azerty" vrai dans ce cas => return True
